const { importPKCS8, importPKCS1, exportJWK } = jose;

(async () => {
  try {
    const raw = msg.dbRecord.recordDetails.files.privateKey.fileContent;
    const pem = typeof raw === "string"
      ? raw
      : (raw && Buffer.isBuffer(raw) ? raw.toString("utf8") : "");

    const domain = String(msg.dbRecord.domain || "").trim();
    const alg = msg.alg || "RS256";

    if (!pem || !pem.includes("PRIVATE KEY")) {
      node.error("PEM private key not found in msg..fileContent", msg);
      return;
    }

    let key;
    if (/-----BEGIN PRIVATE KEY-----/.test(pem)) {
      key = await importPKCS8(pem, alg, { extractable: true });
    } else if (/-----BEGIN RSA PRIVATE KEY-----/.test(pem)) {
      key = await importPKCS1(pem, alg, { extractable: true });
    } else {
      throw new Error("Unsupported PEM format");
    }

    const privateJwk = await exportJWK(key);
    privateJwk.alg = alg;
    privateJwk.use = "sig";
    privateJwk.key_ops = ["sign"];

    const publicJwk = { ...privateJwk };
    delete publicJwk.d; delete publicJwk.p; delete publicJwk.q;
    delete publicJwk.dp; delete publicJwk.dq; delete publicJwk.qi;
    publicJwk.key_ops = ["verify"];


    function getBaseDomain(domain) {
      const parts = domain.split(".");
      if (parts.length > 2) {
        return parts[parts.length - 2];
      } else {
        return parts[0];
      }
    }
    const kid = `did:web:${domain}#key-1`;
    const x5u = `https://${domain}/.well-known/chain.pem`;
    privateJwk.kid = kid;
    publicJwk.kid = kid;
    publicJwk.x5u = x5u;

    msg.payload = { privateJwk, publicJwk };

    msg.data.recordDetails.downloadFiles.privateJwk = {
      fileType: 'privateJwk',
      fileName: 'privateJwk',
      fileContent: privateJwk
    };
    msg.data.recordDetails.downloadFiles.publicJwk = {
      fileType: 'publicJwk',
      fileName: 'publicJwk',
      fileContent: publicJwk
    };

    node.send(msg);
  } catch (err) {
    node.error(`KEY→JWK failed: ${err.message}`, msg);
  }
})();
return null;

function ensureLF(s) {
  s = String(s || "");
  s = s.replace(/\r\n/g, "\n");
  if (s.charCodeAt(0) === 0xFEFF) s = s.slice(1);
  return s.endsWith("\n") ? s : s + "\n";
}

function extractBase64(certLike) {
  let s = String(certLike || "");
  s = s.replace(/\r\n/g, "\n");

  const m = s.match(/-----BEGIN\s+CERTIFICATE-----([\s\S]*?)-----END\s+CERTIFICATE-----/i);
  if (m) s = m[1];

  s = s.replace(/-----BEGIN[^-]+-----/gi, "");
  s = s.replace(/-----END[^-]+-----/gi, "");

  s = s.replace(/[^A-Za-z0-9+/=]/g, "");

  return s;
}

function formatPEM(certLike) {
  const b64 = extractBase64(certLike);
  if (!b64) return "";
  const lines = b64.match(/.{1,64}/g).join("\n");
  return `-----BEGIN CERTIFICATE-----\n${lines}\n-----END CERTIFICATE-----\n`;
}

