function b64urlToUtf8(b64url){
    if (typeof b64url !== 'string') throw new Error('Expected string');
    let s = b64url.replace(/-/g,'+').replace(/_/g,'/');
    const pad = s.length % 4;
    if (pad) s += '='.repeat(4 - pad);
    return Buffer.from(s, 'base64').toString('utf8');
}

