const vcjwt=async () => {
  try {
    const jwk = (({ kty, n, e, d, p, q, dp, dq, qi, kid }) =>
      ({ kty, n, e, d, p, q, dp, dq, qi, kid }))(privJwk);
    const key = await importJWK(jwk, 'RS256');

    try {
      const signer = new GaiaXSignatureSigner({
        privateKey: key,
        privateKeyAlgorithm: "RS256",
        privateKeyAlg: "RS256",
        verificationMethod: vm,
        safe: false
      });
      const vcForLdSign = {
        "@context": [...vc["@context"], "https://w3id.org/security/suites/jws-2020/v1"],
        ...vc
      };
      const vc_ld_signed = await signer.sign(vcForLdSign);
    //   flow.set('participant_vc_ld', vc_ld_signed); 
    } catch (e) {
      node.warn('Participant LD-sign warning: ' + (e?.message || e));
    }

    const jwt = await new SignJWT(vc)
      .setProtectedHeader({
        alg: 'RS256',
        typ: 'vc+jwt',
        cty: 'vc',
        kid: jwk.kid || vm,
        iss: did
      })
      .setIssuer(did)
      .setIssuedAt()
      .sign(key);

    // flow.set('participant_jwt', jwt);
    // node.send({ payload: { ok: true } });
    return jwt

  } catch (e) {
    node.error('Participant VC-JWT sign failed: ' + (e?.message || e), msg);
  }
}

